,\"has_more\":false,\"total_count\":0,\"url\":\"\\/v1\\/charges\\/ch_3NNdYBAZeADNyTCB3RyyYj7O\\/refunds\"},\"review\":null,\"shipping\":null,\"source\":{\"id\":\"card_1NNdYAAZeADNyTCBbB44gc23\",\"object\":\"card\",\"address_city\":null,\"address_country\":null,\"address_line1\":null,\"address_line1_check\":null,\"address_line2\":null,\"address_state\":null,\"address_zip\":null,\"address_zip_check\":null,\"brand\":\"Visa\",\"country\":\"US\",\"customer\":\"cus_O9xThjVfPabkph\",\"cvc_check\":\"pass\",\"dynamic_last4\":null,\"exp_month\":12,\"exp_year\":2024,\"fingerprint\":\"zt1oojDt3KoxYV0S\",\"funding\":\"credit\",\"last4\":\"1111\",\"metadata\":[],\"name\":null,\"tokenization_method\":null,\"wallet\":null},\"source_transfer\":null,\"statement_descriptor\":null,\"statement_descriptor_suffix\":null,\"status\":\"succeeded\",\"transfer_data\":null,\"transfer_group\":null}',	'usd',	0,	1,	0,	'2023-06-27 20:41:28',	'2023-06-27 20:42:17');
